vertex:
#version 120
attribute vec2 a_pos;
uniform mat4 u_posmatrix;
void main ()
{
  vec4 tmpvar_1;
  tmpvar_1.zw = vec2(0.0, 1.0);
  tmpvar_1.xy = a_pos;
  gl_Position = (u_posmatrix * tmpvar_1);
  gl_PointSize = 2.0;
}

fragment:
#version 120
uniform vec4 u_color;
void main ()
{
  gl_FragColor = u_color;
}
